package com.smrc.mdm;

public class Test {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int space=4;
		for (int i = 1; i <= 5; i++) {
			
		for(int j=1;j<=space;j++)	{
			
			System.out.print(" ");
			space--;
		}
for(int k=1;k<=2*i-1;k++)	{
			
			System.out.print("*");
		}
System.out.println(" ");

	}
		
}
}
